import { useState, useEffect} from 'react';
import './App.css';
import restaurant from './images/restaurant.png'
import gasStation from './images/gasoline-pump.png'
function WalletButton(props){
  const [isMetamaskInstalled, setMetamaskInstalled] = useState(true);
  const [isConnected, setConnected] = useState(false);
  const [isLogged, setLogged] = useState(false);
  const [accounts, setAccounts] = useState(null);
  const connectWalletAccount = async () => {
    // to detect whether the wallet is installed
    if (isConnected === false){
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ 
          method: 'eth_requestAccounts',
        });
        setAccounts(accounts); 
        setConnected(true);
        setLogged(true);
      }
    }
    console.log(isLogged);
  }
  const showAccount = () => {
    const prefix = accounts[0].substr(0, 5);
    const suffix = accounts[0].substr(36, 40);
    return (prefix + "..." + suffix);
  }
  useEffect(() => {
      if (typeof window.ethereum === 'undefined'){
        setMetamaskInstalled(false);
      }
  },[]);
  return (
    <div className = "basis-7/12 w-2/5  bg-green-200 ml-auto text-center flex flex-col justify-center rounded-2xl" onClick = {connectWalletAccount}>
        <div>
          {isMetamaskInstalled ? (isLogged ? showAccount() :"Log In") : "Please Install MetaMask"}
        </div>
    </div>
  )
}
function Header(props){
  return (
    <div className = 'w-full h-24 flex flex-row'>
        <div className = 'basis-7/12 bg-yellow-300 flex flex-col justify-center'>
          <div className = 'font-mono text-4xl pl-10'>
            Banana
          </div>
        </div>
        <div className = 'basis-5/12  bg-sky-400 flex flex-col justify-center pr-5'>
            <WalletButton/>
        </div>
    </div>
  )
}
function Item(props){
  const {item, index, setPageName} = props;
  function handleItemClick(){
      console.log(item.name)
      setPageName(item.name);
  }
  return (
    <div className = 'flex flex-col border-2 border-black basis-1/5 h-1/3 mx-8 my-8 rounded-3xl font-mono p-4' onClick = {handleItemClick}>
        <img src = {item.photo} className = 'w-1/5'/>
        <div className = 'basis-1/5 border-b-2 border-black mt-5'>
          {item.name}
        </div>
        <div className = 'text-sm basis-3/5 mt-5'>
          {item.description}
        </div>
    </div>
  )
}
class ButtonProp {
  constructor(name, content){
    this.name = name;
    this.insideContent = content
  }
}
function PageButton(props){
  const {item, index, selectHandler, selected} = props
  function onClickHandler(){
    selectHandler(index);
  }
  return (
    <div className = {`bg-white w-full h-12 font-mono border-r-2 ${selected ? "border-yellow-500" : "border-transparent"}`}>
        <div className = 'w-7/12 h-full mx-auto mt-5 text-center flex flex-col justify-center rounded-lg border-2 border-yellow-200 shadow-lg hover:bg-yellow-100 active:bg-yellow-300' onClick = {onClickHandler}>
            <div>
              {item.name}
            </div>
        </div>
    </div>
  )
}
function GasStationPage(props){
  const [sidebarButtonProps, setSidebarButtonProps] = useState([new ButtonProp('Info', <GasStationInfo/>), new ButtonProp('Interact', <GasStationInteract/>), new ButtonProp('Register'), new ButtonProp('Settings')])
  const [selectedIndex, setSelectedIndex] = useState(1);

  return (
    <div className = 'w-full h-full flex flex-row'>
        <aside className = 'basis-1/6 pt-6 border-r-2 border-gray-200'>
              {
                sidebarButtonProps.map((item, index) => {
                    return <PageButton item = {item} index = {index} selectHandler = {setSelectedIndex} selected = {selectedIndex == index}/>
                })
              }
        </aside>
        <main className = 'basis-5/6 font-mono'>
             {sidebarButtonProps[selectedIndex].insideContent} 
        </main>
    </div>
  )
}
function GasStationInfo(props){
  return (
    <div className = 'w-full h-full p-16'>
        Introducing This Gas Station Page: A Web3-powered Platform for Hassle-free Gas Payment. <br/><br/>

        This Gas Station Page is a decentralized platform that simplifies gas payment for vehicle owners. With the power of Web3 technology, This Gas Station Page enables vehicle owners to register their license plate number and wallet address on the blockchain. This allows for seamless gas payments using any supported cryptocurrency or stablecoin. <br/><br/>

        This Gas Station Page offers a range of features that make gas payment easy and convenient. Vehicle owners can register their license plate number and wallet address on the platform, making it easy to manage their gas payments. They can also update their registration information to avoid any errors. Additionally, This Gas Station Page allows for quick and easy deposits of any supported cryptocurrency or stablecoin into the platform. <br/><br/>

        For gas station operators, This Gas Station Page provides a simple and secure way to receive payments. With the platform's Web3 integration, payments are instantly converted to stablecoins and transferred to This Gas Station Page's account. <br/><br/>

        This Gas Station Page is built using the latest Web3 technology, making it a secure and reliable platform for gas payment. With its user-friendly interface and convenient features, This Gas Station Page is the ideal solution for vehicle owners looking for hassle-free gas payment. <br/><br/>
    </div>
  )
}
function InteractionButton(props){
  return (
    <div className = 'basis-5/12 h-48 bg-yellow-300 my-auto text-center flex flex-col justify-center'>
        <div>
              {props.text}  
        </div>
    </div>
  )
}
class GasStationRecord{
  constructor(year, month, day, currency, gasAmount, location, payment, transaction){
      this.year = year;
      this.month = month;
      this.curreny = currency
      this.gasAmount = gasAmount
      this.location = location
      this.transaction = transaction
  }
}
function GasStationInteract(props){
  const [records, setRecords] = useState([new GasStationRecord(2023, 4, 22, 'DAI', '34.1', "China Gas", "44.5"), new GasStationRecord(2023, 4, 12, 'USDT', '45.6', "China Gas", "34.5")]);
  return (
    <div className = 'w-full h-full p-20 font-mono'>
        <div className = 'w-5/12 mx-auto border-4 h-56 border-yellow-200 flex flex-row justify-around text-3xl'>
            <InteractionButton text = "FULL"/>
            <InteractionButton text = "AUTO"/>
