import logo from './logo.svg';
import { useState, useEffect} from 'react';
import './App.css';
function WalletButton(props){
  const [isMetamaskInstalled, setMetamaskInstalled] = useState(true);
  const [isConnected, setConnected] = useState(false);
  const [isLogged, setLogged] = useState(false);
  const [accounts, setAccounts] = useState(null);
  const connectWalletAccount = async () => {
    // to detect whether the wallet is installed
    if (isConnected === false){
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ 
          method: 'eth_requestAccounts',
        });
        setAccounts(accounts); 
        setConnected(true);
        setLogged(true);
      }
      alert("Successfully Logged in!")
    }
    console.log(isLogged);
  }
  const showAccount = () => {
    const prefix = accounts[0].substr(0, 5);
    const suffix = accounts[0].substr(36, 40);
    return (prefix + "..." + suffix);
  }
  useEffect(() => {
      if (typeof window.ethereum === 'undefined'){
        setMetamaskInstalled(false);
      }
  },[]);
  return (
    <div className = "basis-7/12 w-2/5  bg-green-200 ml-auto text-center flex flex-col justify-center rounded-2xl" onClick = {connectWalletAccount}>
        <div>
          {isMetamaskInstalled ? (isLogged ? showAccount() :"Log In") : "Please Install MetaMask"}
        </div>
    </div>
  )
}
function Header(props){
  return (
    <div className = 'w-full h-24 flex flex-row'>
        <div className = 'basis-7/12 bg-yellow-300 flex flex-col justify-center'>
          <div className = 'font-mono text-4xl pl-10'>
            Banana
          </div>
        </div>
        <div className = 'basis-5/12  bg-sky-400 flex flex-col justify-center pr-5'>
            <WalletButton/>
        </div>
    </div>
  )
}
function Content(props){
  return (
      <div className = 'w-full h-full bg-yellow-200 overflow-y-auto'>

      </div>
  )
}
function App() {
  return (
    <div className = "w-full h-screen container mx-auto">
      <div className = "flex flex-col h-screen">
        <Header/>
        <Content/>
      </div>
    </div>
  );
}

export default App;
